import React, { useEffect, useState } from "react";
import {MenuList, MenuItem, } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'
import AddSharpIcon from '@material-ui/icons/AddSharp'
import FormatColorFillSharpIcon from '@material-ui/icons/FormatColorFillSharp'
import SlideShowSharpIcon from '@material-ui/icons/SlideshowSharp'
import SideBarComponentsList from "./sideBarComponentsList";
import { connect } from 'react-redux'
import { updateCreatorComponentsData } from '../actions'
import { ComponentsMap } from './componentsMap'

const useStyle = makeStyles(theme => ({
    sideBarMenu: {
        display: 'inline-flex',
        flexGrow: 1,
        float: 'right',
        margin: '0 1em'
    }
}))

function SideBar(props) {
  const classes = useStyle()
  const [componentsList, setComponentsList] = useState(['AppBarBasic', 'LandingBasic'])

  useEffect(() => {
    let data = componentsList.map(component => {
      return {
        [component]: ComponentsMap[component].initData
      }
    })
    props.updateCreatorComponentsData(data)
  }, [componentsList, props])

  return (
    <div>
      <MenuList className={classes.sideBarMenu}>
        <MenuItem>
          <SlideShowSharpIcon />
        </MenuItem>
        <MenuItem>
          <FormatColorFillSharpIcon />
        </MenuItem>
        <MenuItem>
          <AddSharpIcon />
        </MenuItem>
      </MenuList>
      <SideBarComponentsList />
    </div>
  );
}

export default connect(null, { updateCreatorComponentsData })(SideBar)
